package com.aiub.lab;

public class Bicycle {
			public int gear;
			public int speed;
			
			

			public Bicycle() {
				super();
				// TODO Auto-generated constructor stub
			}
			
			public Bicycle(int gear, int speed) {
				super();
				this.gear = gear;
				this.speed = speed;
			}

			public int getGear() {
				return gear;
			}

			public void setGear(int gear) {
				this.gear = gear;
			}

			public int getSpeed() {
				return speed;
			}

			public void setSpeed(int speed) {
				this.speed = speed;
			}
			
			

}
